import "core-js/modules/web.url-search-params.delete.js";
import "core-js/modules/web.url-search-params.has.js";
import "core-js/modules/web.url-search-params.size.js";
import * as d3 from 'd3';
import axios from 'axios';
export default {
  name: "D3Runner",
  data() {
    return {
      d3Code: "",
      // 存储用户输入的 D3.js 代码
      pictureUrl: "",
      // 存储图片URL
      noteId: this.$route.query.id || null // 从路由获取 noteId，若无则为新建
    };
  },

  created() {
    if (this.noteId) {
      this.fetchNoteDetails();
    }
  },
  methods: {
    fetchNoteDetails() {
      // 将请求 URL 修改为路径变量形式
      this.$request.get(`/notes/selectById/${this.noteId}`).then(res => {
        if (res.code === '200' && res.data) {
          this.d3Code = res.data.code || ""; // 将 code 放入 d3Code
          this.pictureUrl = res.data.picture || ""; // 将图片 URL 放入 pictureUrl
        } else {
          this.$message.error(res.msg || '无法加载Note详情');
        }
      }).catch(error => {
        console.error("获取 Note 详情失败:", error);
        this.$message.error('获取 Note 详情失败');
      });
    },
    runD3Code() {
      const svgContainer = this.$refs.d3Output;

      // 清空以前的图像
      while (svgContainer.firstChild) {
        svgContainer.removeChild(svgContainer.firstChild);
      }

      // 运行 D3.js 代码
      try {
        const userFunc = new Function('Plot', 'container', this.d3Code);
        userFunc(Plot, svgContainer);
      } catch (error) {
        console.error("D3.js 代码执行出错:", error);
        alert("D3.js 代码执行出错，请检查代码。");
      }
    },
    saveD3Output() {
      const svgElement = this.$refs.d3Output.querySelector('svg');
      if (!svgElement) {
        alert("没有可保存的 D3.js 图形，请先运行代码。");
        return;
      }

      // 将 SVG 转换为 PNG 图片
      const svgData = new XMLSerializer().serializeToString(svgElement);
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');
      const img = new Image();
      const svgBlob = new Blob([svgData], {
        type: 'image/svg+xml;charset=utf-8'
      });
      const url = URL.createObjectURL(svgBlob);
      img.onload = () => {
        // 设置 canvas 大小与 SVG 一致
        canvas.width = img.width;
        canvas.height = img.height;

        // 将 SVG 绘制到 canvas 上
        ctx.drawImage(img, 0, 0);

        // 将 canvas 转换为 PNG
        canvas.toBlob(blob => {
          const formData = new FormData();
          formData.append('file', blob, 'd3-output.png');

          // 上传图片到服务器
          axios.post(this.$baseUrl + '/files/upload', formData).then(response => {
            const pictureUrl = response.data.data;
            console.log('Picture URL:', pictureUrl); // 调试输出
            // 保存或者更新 notes
            const noteData = {
              id: this.noteId,
              comment: 'D3.js 图形',
              code: this.d3Code,
              picture: pictureUrl
            };
            this.$request({
              method: this.noteId ? 'put' : 'post',
              url: this.noteId ? '/notes/update' : '/notes/add',
              data: noteData
            }).then(res => {
              if (res.code === '200') {
                this.$message.success(this.noteId ? 'Note 更新成功' : 'Note 创建成功');
              } else {
                this.$message.error(res.msg || '操作失败');
              }
            }).catch(error => {
              console.error(this.noteId ? '更新 Note 失败:' : '创建 Note 失败:', error);
              this.$message.error('操作失败，请稍后再试');
            });
          }).catch(error => {
            console.error("上传图片失败:", error);
          });
        });
      };
      img.src = url; // 触发图像加载
    }
  }
};
